{
  "swagger": "2.0",
  "info": {
    "description": "The HTTP API for Event Store",
    "version": "5.0.0",
    "title": "HTTP API",
    "contact": {
      "email": "chris.ward@eventstore.org"
    },
    "license": {
      "name": "Apache 2.0",
      "url": "http://www.apache.org/licenses/LICENSE-2.0.html"
    }
  },
  "host": "eventstore.org",
  "securityDefinitions": {
    "basicAuth": {
      "type": "basic"
    }
  },
  "schemes": [
    "https"
  ],
  "paths": {
    "/admin/shutdown": {
      "post": {
        "description": "Issues a shut down command to a node.",
        "summary": "Shutdown a node",
        "tags": [
          "Admin"
        ],
        "operationId": "Shutdown a node",
        "produces": [
          "application/json"
        ],
        "parameters": [],
        "responses": {
          "200": {
            "description": "OK"
          }
        },
        "security": [
          {
            "basicAuth": []
          }
        ]
      }
    },
    "/admin/scavenge": {
      "post": {
        "description": "Scavenge reclaims disk space by rewriting database chunks, minus the events to delete, and then deleting the old chunks.",
        "summary": "Scavenge a node",
        "tags": [
          "Admin"
        ],
        "operationId": "Scavenge a node",
        "produces": [
          "application/json"
        ],
        "parameters": [
          {
            "name": "startFromChunk",
            "in": "query",
            "description": "The chunk ID to start the scavenge operation from.",
            "type": "integer",
            "required": false
          },
          {
            "name": "threads",
            "in": "query",
            "description": "The number of threads to run the scavenge operation on (max 4).",
            "type": "integer",
            "required": false,
            "default": 1
          }
        ],
        "responses": {
          "200": {
            "description": "OK"
          },
          "401": {
            "description": "Unauthorized"
          }
        },
        "security": [
          {
            "basicAuth": []
          }
        ]
      }
    },
    "/admin/scavenge/{scavengeId}": {
      "delete": {
        "description": "Stop a running scavenge operation.",
        "summary": "Stop a scavenge operation",
        "tags": [
          "Admin"
        ],
        "operationId": "Stop a scavenge",
        "produces": [
          "application/json"
        ],
        "parameters": [
          {
            "name": "scavengeId",
            "in": "path",
            "description": "The scavenge ID",
            "required": true,
            "type": "integer"
          }
        ],
        "responses": {
          "200": {
            "description": "OK"
          },
          "401": {
            "description": "Unauthorized"
          }
        },
        "security": [
          {
            "basicAuth": []
          }
        ]
      }
    },
    "/admin/mergeindexes": {
      "delete": {
        "description": "Manually merge indexes after a scavenge operation",
        "summary": "Merge indexes",
        "tags": [
          "Admin"
        ],
        "operationId": "Merge Indexes",
        "produces": [
          "application/json"
        ],
        "responses": {
          "200": {
            "description": "OK"
          },
          "401": {
            "description": "Unauthorized"
          }
        },
        "security": [
          {
            "basicAuth": []
          }
        ]
      }
    },
    "/info": {
      "get": {
        "description": "Returns information about node.",
        "summary": "Get info for node",
        "tags": [
          "Info"
        ],
        "operationId": "Get info for node",
        "produces": [
          "application/json"
        ],
        "parameters": [],
        "responses": {
          "200": {
            "description": "OK"
          },
          "401": {
            "description": "Unauthorized"
          }
        },
        "security": [
          {
            "basicAuth": []
          }
        ]
      }
    },
