          "Info"
        ],
        "operationId": "Get configuration for node",
        "produces": [
          "application/json"
        ],
        "parameters": [],
        "responses": {
          "200": {
            "description": "OK"
          },
          "401": {
            "description": "Unauthorized"
          }
        },
        "security": [
          {
            "basicAuth": []
          }
        ]
      }
    },
    "/streams/{stream}": {
      "get": {
        "summary": "Reads a stream",
        "tags": [
          "Streams"
        ],
        "description": "Read a stream, receiving a standard AtomFeed document as a response.",
        "operationId": "Read a stream",
        "produces": [
          "application/json",
          "application/xml"
        ],
        "parameters": [
          {
            "name": "stream",
            "in": "path",
            "description": "The stream ID",
            "required": true,
            "type": "string"
          },
          {
            "name": "embed",
            "in": "query",
            "required": false,
            "type": "string",
            "enum": [
              "None",
              "Content",
              "Rich",
              "Body",
              "PrettyBody",
              "TryHarder"
            ]
          }
        ],
        "responses": {
          "200": {
            "description": "OK"
          },
          "404": {
            "description": "Not found"
          }
        }
      },
      "post": {
        "summary": "Write to a stream",
        "tags": [
          "Streams"
        ],
        "description": "Write to a stream.",
        "operationId": "Write to a stream",
        "produces": [
          "application/json",
          "application/xml"
        ],
        "parameters": [
          {
            "name": "stream",
            "in": "path",
            "description": "The name of the stream",
            "required": true,
            "type": "string"
          },
          {
            "in": "body",
            "name": "stream data",
            "description": "Stream events to create",
            "required": true,
            "schema": {
              "$ref": "#/definitions/streamData"
            }
          },
          {
            "$ref": "#/parameters/ES-ExpectedVersion"
          },
          {
            "$ref": "#/parameters/ES-EventType"
          },
          {
            "$ref": "#/parameters/ES-EventId"
          },
          {
            "$ref": "#/parameters/ES-RequiresMaster"
          }
        ],
        "responses": {
          "201": {
            "description": "New stream created"
          },
          "307": {
            "description": "Temporary Redirect"
          },
          "400": {
            "description": "Write request body invalid"
          }
        }
      },
      "delete": {
        "summary": "Deletes a stream",
        "tags": [
          "Streams"
        ],
        "description": "Delete specified stream",
        "operationId": "Delete a stream",
        "produces": [
          "application/xml",
          "application/json"
        ],
        "parameters": [
          {
            "name": "stream",
            "in": "path",
            "description": "The stream ID to delete",
            "required": true,
            "type": "string"
          }
        ],
        "responses": {
          "204": {
            "description": "Stream deleted"
          }
        }
      }
    },
    "/streams/{stream}/incoming/{guid}": {
      "post": {
        "tags": [
          "Streams"
        ],
        "summary": "An alternative URL to post events to",
        "description": "A URL generated by Event Store if you don't supply an ID when creating a stream. You then use this URL to post events to.",
