        "produces": [
          "application/json",
          "application/xml"
        ],
        "parameters": [
          {
            "name": "stream",
            "in": "path",
            "description": "The name of the stream",
            "required": true,
            "type": "string"
          },
          {
            "name": "guid",
            "in": "path",
            "description": "Autogenerated UUID",
            "required": true,
            "type": "string"
          }
        ],
        "responses": {
          "200": {
            "description": "New event created"
          },
          "400": {
            "description": "Bad request"
          }
        }
      }
    },
    "/streams/{stream}/{event}": {
      "get": {
        "tags": [
          "Streams"
        ],
        "summary": "Read a stream event",
